// ------------------- MOBILE NAVIGATION -------------------
const btnElement = document.querySelector('#hamburgerBtn');
const navElement = document.querySelector('#primaryNav');

btnElement.addEventListener('click', () => {
  btnElement.classList.toggle('open');
  navElement.classList.toggle('open');
});

// ------------------- CARD FLIP FUNCTION -------------------
document.querySelectorAll('.flip-card-inner').forEach(card => {
  card.addEventListener('click', (e) => {
    // Prevent flipping if clicking inside the back sideâ€™s button
    if (e.target.classList.contains('read-more')) return;
    card.classList.toggle('flipped');
  });
});

// ------------------- READ MORE TOGGLE -------------------
document.querySelectorAll('.read-more').forEach(button => {
  button.addEventListener('click', (e) => {
    e.stopPropagation(); // prevent card flip
    const moreText = button.closest('.flip-card-back').querySelector('.more-text');
    const isVisible = moreText.style.display === 'block';

    moreText.style.display = isVisible ? 'none' : 'block';
    button.textContent = isVisible ? 'Read More' : 'Read Less';
  });
});

